"use strict";

$(document).ready(function () {
    var $accordionItems = $(".accordion > li > ul").hide();

    $(".accordion > li > a").click(function () {
        var $target = $(this).next();

        if (!$target.hasClass("active")) {
            $accordionItems.removeClass("active").slideUp();
            $target.addClass("active").slideDown();
        }

        return false;
    });

    $(".product-links-wap a").click(function () {
        var $imageSrc = $(this).children("img").attr("src");
        $("#product-detail").attr("src", $imageSrc);

        return false;
    });

    $("#btn-minus").click(function () {
        var $value = $("#var-value").html();
        $value = $value === "1" ? $value : $value - 1;
        $("#var-value").html($value);
        $("#product-quanity").val($value);

        return false;
    });

    $("#btn-plus").click(function () {
        var $value = $("#var-value").html();
        $value++;
        $("#var-value").html($value);
        $("#product-quanity").val($value);

        return false;
    });

    $(".btn-size").click(function () {
        var $size = $(this).html();

        $("#product-size").val($size);

        $(".btn-size").removeClass("btn-secondary");
        $(".btn-size").addClass("btn-success");

        $(this).removeClass("btn-success");
        $(this).addClass("btn-secondary");

        return false;
    });
});